<script>
    $(document).ready(function() {
        const $sideMenu = $(".side_menu_big");
        const $sideMenuSmall = $(".side_menu_small");
        const $btnCloseMenu = $(".btn_close_menu");
        const $btnSmallMenu = $(".btn_small_menu");

        // Função para fechar o menu grande e abrir o menu pequeno
        $btnCloseMenu.on("click", function() {
            $sideMenu.addClass("menu_hidden").removeClass("menu_visible");
            $sideMenuSmall.addClass("menu_visible").removeClass("menu_hidden");
        });

        // Função para fechar o menu pequeno e abrir o menu grande
        $btnSmallMenu.on("click", function() {
            $sideMenuSmall.addClass("menu_hidden").removeClass("menu_visible");
            $sideMenu.addClass("menu_visible").removeClass("menu_hidden");
        });
    });

</script>
