<script>
    const userId = @json($userId);
    let chatbotSet = ''
    let hashHexSet = ''

    async function generateUrl() {
        const encoder = new TextEncoder();
        const data = encoder.encode(userId);
        const hashBuffer = await crypto.subtle.digest('SHA-256', data);
        const hashArray = Array.from(new Uint8Array(hashBuffer));
        const hashHex = hashArray.map(b => b.toString(16).padStart(2, '0')).join('');

        const url = 'https://bw8.hook.app.br/webhooksheets/api/' + hashHex
        hashHexSet = hashHex

        document.getElementById('generatedUrl').value = url;
    }

    function setChatbot(chatbot) {
        chatbotSet = chatbot
        const buttonOpenai = document.getElementById('openai');
        const buttonMentoria = document.getElementById('mentoria');
        if(chatbot == 'OpenAI') {
            buttonOpenai.style.backgroundColor = '#0d6efd';
            buttonMentoria.style.backgroundColor = '#F1F2F4';
        }
        else if(chatbot == 'MentorIA') {
            buttonOpenai.style.backgroundColor = '#F1F2F4';
            buttonMentoria.style.backgroundColor = '#0d6efd';
        }
    }

    async function salvar() {
        if(chatbotSet != 'OpenAI') {
            return
        }
        try {
            const response = await fetch('https://dev-ia2.hook.app.br/api/chatbotia/sheets/createupdate', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ 'userIdHash': hashHexSet })
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const responseData = await response.json();
            console.log('Success:', responseData);
        } catch (error) {
            console.error('Error:', error);
        }
    }
</script>
<section>
    <div>
        <p class="mt-1 text-sm text-gray-600">
            {{ __('GERAR WEBHOOK - GOOGLE SHEETS') }}
        </p>
        <div style="display: flex; align-items: center;">
            <button type="button" onclick="generateUrl()" class="btn btn-primary">GERAR URL</button>
            <input type="text" placeholder="Url" id="generatedUrl" class="ml-2 form-control" style="margin-left: 10px; flex-grow: 1;" readonly />
        </div>
    </div>
    <div style="padding-bottom: 20px">
    </div>
